# 7×7 右箭头
for i in range(7):
    row = ""
    for j in range(7):
        if (i == 0 and j == 0) or (i == 1 and j == 1) or (i == 2 and j == 2) or i == 3 or (i == 4 and j == 2) or (i == 5 and j == 1) or (i == 6 and j == 0):
            row += "1"
        else:
            row += "0"
    print(row)
